SETTINGS.TEMPLATESCRIPT_18:
//this uses a plain html table as the data source, header row + no row label + multirows
var da = [];
var collabels = [];
var rowlabels = [];
var options = {};
if(@@style@@=='ct-fixed-widths'){
options.width = opts['width'];
options.height = opts['height'];
}

//extract an array of values from each row(tr) as a 2d array of values (da)
$('#' + @@AUTOID@@ + '_datatable > table tr').each(function(i, the_tr){
	var onerow =[];
	var cells= $(the_tr).find('td');
	if(cells.length>0){
		 cells.each(function(j,the_cell){onerow.push($(the_cell).text())});
		 da.push(onerow);
	};
});

//extract an array of values from the head row(th) as col labels (collabels)
var headcells = $('#' + @@AUTOID@@ + '_datatable > table th');
if(headcells.length>0){
	 headcells.each(function(n,the_hcell){collabels.push($(the_hcell).text())});
};


//if rowlabels = true, we extract row labels from the first col of each row
//and remove the first col of the head row. 
if(opts['rowlabels']=='yes'){
	 //remove the first col into labels 
	 $().each(da,function(index,item){
	  rowlabels.push(item.shift())
	 });
	 //remove the first col of head, because its useless
	 if(collabels.length>0){
	  collabels.shift();
	 } 
}

var data = {
  labels: collabels
};

switch (@@charttype@@){
 case 'line':
    data.series = da;
   new Chartist.Line('#' + @@AUTOID@@ + '_chart', data, options);
   break;
case 'bar':
   data.series = da;
   new Chartist.Bar('#' + @@AUTOID@@ + '_chart', data, options);
   break;
case 'pie':
   data.series = da.length>0 ? da[0] : [];
   new Chartist.Pie('#' + @@AUTOID@@ + '_chart', data, options);
   break;
case 'donut':
    data.series = da.length>0 ? da[0] : [];
    options.donut = true;
   new Chartist.Pie('#' + @@AUTOID@@ + '_chart', data, options);
   break;
}

