SETTINGS.TEMPLATESCRIPT_23:
//extract an array of values from each row(tr)
var steps =[];
var stepindex=0;
$('#' + @@AUTOID@@ + '_datatable > table tr').each(function(i, the_tr){
	var cells= $(the_tr).find('td');
	if(cells.length>2){
             var onestep = {};
              onestep['element'] = $(cells[0]).text();
              onestep['intro'] = $(cells[1]).text();
              onestep['position'] = $(cells[2]).text();
              steps[stepindex]=onestep;
              stepindex++;
	};
});

$('#' + @@AUTOID@@ ).click(function(){
        //var intro = introJs();
var intro = requiredjs_introjs();
          intro.setOptions({
            steps: steps,
            nextLabel: 'next',
prevLabel: 'previous,
skipLabel: 'close',
doneLabel: 'end',
          });
          intro.start();
      });

